mapper.select:
SELECT


,yonghu.yonghu_name as yonghuName
,yonghu.yonghu_phone as yonghuPhone
,yonghu.yonghu_id_number as yonghuIdNumber
,yonghu.yonghu_photo as yonghuPhoto
,yonghu.yonghu_email as yonghuEmail
,yonghu.yonghu_delete as yonghuDelete
,ziliao.ziliao_uuid_number as ziliaoUuidNumber
,ziliao.ziliao_name as ziliaoName
,ziliao.ziliao_photo as ziliaoPhoto
,ziliao.ziliao_file as ziliaoFile
,ziliao.ziliao_video as ziliaoVideo
,ziliao.zan_number as zanNumber
,ziliao.cai_number as caiNumber
,ziliao.ziliao_types as ziliaoTypes
,ziliao.ziliao_clicknum as ziliaoClicknum
,ziliao.ziliao_content as ziliaoContent
,ziliao.ziliao_delete as ziliaoDelete

FROM ziliao_collection  a
left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id
left JOIN ziliao ziliao ON a.ziliao_id = ziliao.id



        and a.id in

        #{item}



        and (
            a.ziliao_id = #{params.ziliaoId}
        )


        and (
            a.yonghu_id = #{params.yonghuId}
        )


        and a.ziliao_collection_types = #{params.ziliaoCollectionTypes}


          and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


          and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 




        and a.yonghu_id IS NOT NULL


        and yonghu.yonghu_name like CONCAT('%',#{params.yonghuName},'%')


        and yonghu.yonghu_phone like CONCAT('%',#{params.yonghuPhone},'%')


        and yonghu.yonghu_id_number like CONCAT('%',#{params.yonghuIdNumber},'%')


        and yonghu.yonghu_email like CONCAT('%',#{params.yonghuEmail},'%')


          and yonghu.yonghu_delete >= #{params.yonghuDeleteStart}   


          and yonghu.yonghu_delete <= #{params.yonghuDeleteEnd}   


        and yonghu.yonghu_delete = #{params.yonghuDelete}


          and UNIX_TIMESTAMP(yonghu.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


          and UNIX_TIMESTAMP(yonghu.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 



        and a.ziliao_id IS NOT NULL


        and ziliao.ziliao_uuid_number like CONCAT('%',#{params.ziliaoUuidNumber},'%')


        and ziliao.ziliao_name like CONCAT('%',#{params.ziliaoName},'%')


        and ziliao.ziliao_video like CONCAT('%',#{params.ziliaoVideo},'%')


          and ziliao.zan_number >= #{params.zanNumberStart}   


          and ziliao.zan_number <= #{params.zanNumberEnd}   


        and ziliao.zan_number = #{params.zanNumber}


          and ziliao.cai_number >= #{params.caiNumberStart}   


          and ziliao.cai_number <= #{params.caiNumberEnd}   


        and ziliao.cai_number = #{params.caiNumber}


        and ziliao.ziliao_types = #{params.ziliaoTypes}



          and ziliao.ziliao_clicknum >= #{params.ziliaoClicknumStart}   


          and ziliao.ziliao_clicknum <= #{params.ziliaoClicknumEnd}   


        and ziliao.ziliao_clicknum = #{params.ziliaoClicknum}


        and ziliao.ziliao_content like CONCAT('%',#{params.ziliaoContent},'%')


          and ziliao.ziliao_delete >= #{params.ziliaoDeleteStart}   


          and ziliao.ziliao_delete <= #{params.ziliaoDeleteEnd}   


        and ziliao.ziliao_delete = #{params.ziliaoDelete}


          and UNIX_TIMESTAMP(ziliao.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


          and UNIX_TIMESTAMP(ziliao.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 


order by a.${params.sort} ${params.order}
