mapper.select:
SELECT



FROM ziliao  a



        and a.id in

        #{item}



        and a.ziliao_uuid_number like CONCAT('%',#{params.ziliaoUuidNumber},'%')


        and a.ziliao_name like CONCAT('%',#{params.ziliaoName},'%')


        and a.ziliao_video like CONCAT('%',#{params.ziliaoVideo},'%')


          and a.zan_number >= #{params.zanNumberStart}   


          and a.zan_number <= #{params.zanNumberEnd}   


        and a.zan_number = #{params.zanNumber}


          and a.cai_number >= #{params.caiNumberStart}   


          and a.cai_number <= #{params.caiNumberEnd}   


        and a.cai_number = #{params.caiNumber}


        and a.ziliao_types = #{params.ziliaoTypes}


          and a.ziliao_clicknum >= #{params.ziliaoClicknumStart}   


          and a.ziliao_clicknum <= #{params.ziliaoClicknumEnd}   


        and a.ziliao_clicknum = #{params.ziliaoClicknum}


        and a.ziliao_content like CONCAT('%',#{params.ziliaoContent},'%')


          and a.ziliao_delete >= #{params.ziliaoDeleteStart}   


          and a.ziliao_delete <= #{params.ziliaoDeleteEnd}   


        and a.ziliao_delete = #{params.ziliaoDelete}


          and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


          and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 



order by a.${params.sort} ${params.order}
